from abc import ABC, abstractmethodclass Animal(ABC):    def __init__(self):        print("In parent class")    @abstractmethod    def move(self):        passclass Person(Animal):    def __init__(self, name):        self.name = name        print(name)    def move(self):        print("i can walk")    def print_message(self):        print("Inheriting Animal class")class Bird(Animal):    def move(self):        print("I can fly")class Penguin(Person, Bird):    def __init__(self):        self.__name = "Pingu"    def getname(self):        return self.__name    def setname(self, name):        self.__name = name    def __update(self):        print("private")penguin1 = Penguin()penguin1.getname()penguin1.setname("Pingu2")penguin1.getname()name = penguin1.getname()print(name)